# test 1
pred_state=['']
real_state=['..................................................................................................................................................................']

# test_2
pred_state.append('')
real_state.append('')

for x in range(len(pred_state)):
	total, positive = 0, 0
	real_state[x] = real_state[x].upper()
	for y in range(len(pred_state[x])):
		if real_state[x][y] == '.': continue
		total += 1
		if real_state[x][y] == pred_state[x][y]:
			positive += 1
	accuracy = float(positive)/total
	with open("../data/Accuracy.txt","a") as ofile:
		ofile.write("Total Number of Sites: %d\nCorrect Predicted Sites: %d\nAccuracy: %f\n\n" % (total, positive, accuracy))